import React, { useState } from 'react';
import { Button, Container, Row, Col, Image } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import { fetchOnePrinter } from '../http/printerAPI';
import EditPrinterModal from '../components/EditPrinterModal';

const PrinterPage = () => {
    const [printer, setPrinter] = useState(null);
    const [showEditModal, setShowEditModal] = useState(false);
    const { id } = useParams();

    useEffect(() => {
        fetchOnePrinter(id).then(data => setPrinter(data));
    }, [id]);

    if (!printer) return <div>Загрузка...</div>;

    return (
        <Container className="mt-4">
            <Row>
                <Col md={8}>
                    <h2>{printer.name}</h2>
                    <Image src={process.env.REACT_APP_API_URL + printer.img} fluid />
                </Col>
                <Col md={4}>
                    <Button
                        variant="primary"
                        onClick={() => setShowEditModal(true)}
                    >
                        Редактировать
                    </Button>
                </Col>
            </Row>

            <EditPrinterModal
                show={showEditModal}
                onHide={() => setShowEditModal(false)}
                printer={printer}
            />
        </Container>
    );
};

export default PrinterPage;
