import React, {useContext, useEffect} from 'react';
import {Container} from "react-bootstrap";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import TypeBar from "../components/TypeBar";
import BrandBar from "../components/BrandBar";
import PrinterList from "../components/PrinterList";
import {observer} from "mobx-react-lite";
import {Context} from "../index";
import {fetchBrands, fetchPrinters, fetchTypes} from "../http/printerAPI";
import Pages from "../components/Pages";

const Shop = observer(() => {
    const {printer} = useContext(Context)

    useEffect(() => {
        fetchTypes().then(data => printer.setTypes(data))
        fetchBrands().then(data => printer.setBrands(data))
        fetchPrinters(null, null, 1, 2).then(data => {
            printer.setPrinters(data.rows)
            printer.setTotalCount(data.count)
        })
    }, [])

    useEffect(() => {
        fetchPrinters(printer.selectedType.id, printer.selectedBrand.id, printer.page, 2).then(data => {
            printer.setPrinters(data.rows)
            printer.setTotalCount(data.count)
        })
    }, [printer.page, printer.selectedType, printer.selectedBrand,])

    return (
        <Container>
            <Row className="mt-2">
                <Col md={3}>
                    <TypeBar/>
                </Col>
                <Col md={9}>
                    <BrandBar/>
                    <PrinterList/>
                    <Pages/>
                </Col>
            </Row>
        </Container>
    );
});

export default Shop;
