import React, { useContext, useEffect, useState } from 'react';
import { Modal, Button, Form, Row, Col, Dropdown } from 'react-bootstrap';
import { Context } from '../../index';
import { observer } from 'mobx-react-lite';
import {
    createPrinter,
    updatePrinter,
    fetchBrands,
    fetchPrint_Technologys as fetchPrintTechnologies
} from '../../http/printerAPI';

const CreatePrinter = observer(({ show, onHide, printerToEdit }) => {
    const { printer } = useContext(Context);
    const [name, setName] = useState('');
    const [price, setPrice] = useState(0);
    const [file, setFile] = useState(null);
    const [info, setInfo] = useState([]);
    const [preview, setPreview] = useState(null);
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        fetchBrands().then(data => printer.setBrands(data));
        fetchPrintTechnologies().then(data => printer.setPrintTechnologies(data));

        if (printerToEdit) {
            setName(printerToEdit.name);
            setPrice(printerToEdit.price);
            setInfo(printerToEdit.info || []);
            setPreview(process.env.REACT_APP_API_URL + printerToEdit.img);
            printer.setSelectedBrand(printerToEdit.brand || {});
            printer.setSelectedPrintTechnology(printerToEdit.printTechnology || {});
        } else {
            resetForm();
        }
    }, [printerToEdit]);

    const resetForm = () => {
        setName('');
        setPrice(0);
        setFile(null);
        setInfo([]);
        setPreview(null);
        printer.setSelectedBrand({});
        printer.setSelectedPrintTechnology({});
    };

    const selectFile = e => {
        const selectedFile = e.target.files[0];
        if (selectedFile) {
            setFile(selectedFile);
            const reader = new FileReader();
            reader.onload = () => setPreview(reader.result);
            reader.readAsDataURL(selectedFile);
        }
    };

    const addInfo = () => {
        setInfo([...info, { title: '', description: '', number: Date.now() }]);
    };

    const removeInfo = (number) => {
        setInfo(info.filter(i => i.number !== number));
    };

    const changeInfo = (key, value, number) => {
        setInfo(info.map(i => i.number === number ? { ...i, [key]: value } : i));
    };

    const handleSubmit = async () => {
        if (!name || !price || !printer.selectedBrand.id || !printer.selectedPrintTechnology.id) {
            alert('Заполните все обязательные поля!');
            return;
        }

        setIsLoading(true);
        const formData = new FormData();
        formData.append('name', name);
        formData.append('price', `${price}`);
        if (file) formData.append('img', file);
        formData.append('brandId', printer.selectedBrand.id);
        formData.append('printTechnologyId', printer.selectedPrintTechnology.id);
        formData.append('info', JSON.stringify(info));

        try {
            if (printerToEdit) {
                await updatePrinter(printerToEdit.id, formData);
            } else {
                await createPrinter(formData);
            }
            onHide();
            resetForm();
        } catch (e) {
            alert(e.response?.data?.message || 'Произошла ошибка');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <Modal show={show} onHide={onHide} size="lg" centered>
            <Modal.Header closeButton>
                <Modal.Title>
                    {printerToEdit ? 'Редактировать принтер' : 'Добавить новый принтер'}
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Row className="mb-3">
                        <Col md={6}>
                            <Dropdown>
                                <Dropdown.Toggle variant="outline-secondary" className="w-100">
                                    {printer.selectedBrand.name || "Выберите бренд"}
                                </Dropdown.Toggle>
                                <Dropdown.Menu className="w-100">
                                    {printer.brands.map(brand => (
                                        <Dropdown.Item
                                            key={brand.id}
                                            onClick={() => printer.setSelectedBrand(brand)}
                                        >
                                            {brand.name}
                                        </Dropdown.Item>
                                    ))}
                                </Dropdown.Menu>
                            </Dropdown>
                        </Col>
                        <Col md={6}>
                            <Dropdown>
                                <Dropdown.Toggle variant="outline-secondary" className="w-100">
                                    {printer.selectedPrintTechnology.name || "Выберите технологию печати"}
                                </Dropdown.Toggle>
                                <Dropdown.Menu className="w-100">
                                    {printer.printTechnologies.map(tech => (
                                        <Dropdown.Item
                                            key={tech.id}
                                            onClick={() => printer.setSelectedPrintTechnology(tech)}
                                        >
                                            {tech.name}
                                        </Dropdown.Item>
                                    ))}
                                </Dropdown.Menu>
                            </Dropdown>
                        </Col>
                    </Row>

                    <Form.Group className="mb-3">
                        <Form.Control
                            value={name}
                            onChange={e => setName(e.target.value)}
                            placeholder="Название принтера"
                        />
                    </Form.Group>

                    <Form.Group className="mb-3">
                        <Form.Control
                            type="number"
                            value={price}
                            onChange={e => setPrice(Number(e.target.value))}
                            placeholder="Цена"
                        />
                    </Form.Group>

                    <Form.Group className="mb-3">
                        <Form.Label>Изображение принтера</Form.Label>
                        <Form.Control
                            type="file"
                            onChange={selectFile}
                            accept="image/*"
                        />
                        {preview && (
                            <div className="mt-2 text-center">
                                <img
                                    src={preview}
                                    alt="Предпросмотр"
                                    style={{ maxHeight: '150px' }}
                                    className="img-thumbnail"
                                />
                            </div>
                        )}
                    </Form.Group>

                    <div className="d-flex justify-content-between align-items-center mb-3">
                        <h5 className="mb-0">Характеристики:</h5>
                        <Button variant="outline-primary" size="sm" onClick={addInfo}>
                            + Добавить
                        </Button>
                    </div>

                    {info.map(i => (
                        <Row key={i.number} className="mb-2 g-2">
                            <Col md={5}>
                                <Form.Control
                                    value={i.title}
                                    onChange={(e) => changeInfo('title', e.target.value, i.number)}
                                    placeholder="Название характеристики"
                                />
                            </Col>
                            <Col md={5}>
                                <Form.Control
                                    value={i.description}
                                    onChange={(e) => changeInfo('description', e.target.value, i.number)}
                                    placeholder="Значение"
                                />
                            </Col>
                            <Col md={2}>
                                <Button
                                    variant="outline-danger"
                                    onClick={() => removeInfo(i.number)}
                                    className="w-100"
                                >
                                    Удалить
                                </Button>
                            </Col>
                        </Row>
                    ))}
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-secondary" onClick={onHide}>
                    Отмена
                </Button>
                <Button variant="success" onClick={handleSubmit} disabled={isLoading}>
                    {isLoading ? 'Сохранение...' : (printerToEdit ? 'Обновить' : 'Добавить')}
                </Button>
            </Modal.Footer>
        </Modal>
    );
});

export default CreatePrinter;
