import React, { useState, useEffect } from 'react';
import { Button, Card, Container, Form } from 'react-bootstrap';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchOnePrinter, updatePrinter } from '../http/printerAPI';
import { fetchBrands, fetchPrintTechnologies } from '../http/deviceAPI';

const EditPrinter = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [name, setName] = useState('');
    const [price, setPrice] = useState(0);
    const [file, setFile] = useState(null);
    const [brandId, setBrandId] = useState('');
    const [printTechnologyId, setPrintTechnologyId] = useState('');
    const [info, setInfo] = useState([]);
    const [brands, setBrands] = useState([]);
    const [printTechnologies, setPrintTechnologies] = useState([]);

    useEffect(() => {
        // Загружаем данные принтера
        fetchOnePrinter(id).then(data => {
            setName(data.name);
            setPrice(data.price);
            setBrandId(data.brandId);
            setPrintTechnologyId(data.printTechnologyId);
            setInfo(data.info);
        });

        // Загружаем бренды и технологии
        fetchBrands().then(data => setBrands(data));
        fetchPrintTechnologies().then(data => setPrintTechnologies(data));
    }, [id]);

    const addInfo = () => {
        setInfo([...info, { title: '', description: '', number: Date.now() }]);
    };

    const removeInfo = (number) => {
        setInfo(info.filter(item => item.number !== number));
    };

    const changeInfo = (key, value, number) => {
        setInfo(info.map(item => item.number === number ? { ...item, [key]: value } : item));
    };

    const selectFile = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append('name', name);
        formData.append('price', `${price}`);
        formData.append('brandId', brandId);
        formData.append('printTechnologyId', printTechnologyId);
        formData.append('info', JSON.stringify(info));
        if (file) {
            formData.append('img', file);
        }

        try {
            await updatePrinter(id, formData);
            navigate('/admin');
        } catch (e) {
            alert(e.response.data.message);
        }
    };

    return (
        <Container className="d-flex flex-column">
            <Card className="p-5">
                <h2 className="m-auto">Редактировать принтер</h2>
                <Form className="d-flex flex-column" onSubmit={handleSubmit}>
                    <Form.Control
                        placeholder="Название принтера"
                        value={name}
                        onChange={e => setName(e.target.value)}
                        className="mt-3"
                    />
                    <Form.Control
                        placeholder="Цена"
                        type="number"
                        value={price}
                        onChange={e => setPrice(Number(e.target.value))}
                        className="mt-3"
                    />
                    <Form.Select
                        value={brandId}
                        onChange={e => setBrandId(e.target.value)}
                        className="mt-3"
                    >
                        <option value="">Выберите бренд</option>
                        {brands.map(brand =>
                            <option key={brand.id} value={brand.id}>
                                {brand.name}
                            </option>
                        )}
                    </Form.Select>
                    <Form.Select
                        value={printTechnologyId}
                        onChange={e => setPrintTechnologyId(e.target.value)}
                        className="mt-3"
                    >
                        <option value="">Выберите технологию печати</option>
                        {printTechnologies.map(tech =>
                            <option key={tech.id} value={tech.id}>
                                {tech.name}
                            </option>
                        )}
                    </Form.Select>
                    <Form.Control
                        type="file"
                        onChange={selectFile}
                        className="mt-3"
                    />
                    <hr className="mt-4"/>
                    <Button
                        variant="outline-dark"
                        onClick={addInfo}
                    >
                        Добавить характеристику
                    </Button>
                    {info.map(i =>
                        <div key={i.number} className="d-flex mt-2">
                            <Form.Control
                                placeholder="Название характеристики"
                                value={i.title}
                                onChange={e => changeInfo('title', e.target.value, i.number)}
                            />
                            <Form.Control
                                placeholder="Описание"
                                value={i.description}
                                onChange={e => changeInfo('description', e.target.value, i.number)}
                                className="mx-2"
                            />
                            <Button
                                variant="outline-danger"
                                onClick={() => removeInfo(i.number)}
                            >
                                Удалить
                            </Button>
                        </div>
                    )}
                    <Button type="submit" variant="outline-success" className="mt-3">
                        Сохранить изменения
                    </Button>
                </Form>
            </Card>
        </Container>
    );
};

export default EditPrinter;
