import React, { useContext, useEffect, useState } from 'react';
import { Modal, Button, Form, Row, Col } from 'react-bootstrap';
import { Context } from '../index';
import { observer } from 'mobx-react-lite';
import { updatePrinter } from '../http/printerAPI';

const EditPrinterModal = observer(({ show, onHide, printer }) => {
    const { device } = useContext(Context);
    const [name, setName] = useState('');
    const [price, setPrice] = useState(0);
    const [file, setFile] = useState(null);

    useEffect(() => {
        if (printer) {
            setName(printer.name);
            setPrice(printer.price);
            // Другие поля инициализации
        }
    }, [printer]);

    const handleUpdate = async () => {
        const formData = new FormData();
        formData.append('name', name);
        formData.append('price', `${price}`);
        if (file) formData.append('img', file);

        try {
            await updatePrinter(printer.id, formData);
            onHide();
        } catch (e) {
            alert(e.response?.data?.message);
        }
    };

    return (
        <Modal show={show} onHide={onHide} size="lg" centered>
            <Modal.Header closeButton>
                <Modal.Title>Редактировать принтер</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group className="mb-3">
                        <Form.Label>Название</Form.Label>
                        <Form.Control
                            value={name}
                            onChange={e => setName(e.target.value)}
                        />
                    </Form.Group>

                    <Form.Group className="mb-3">
                        <Form.Label>Цена</Form.Label>
                        <Form.Control
                            type="number"
                            value={price}
                            onChange={e => setPrice(Number(e.target.value))}
                        />
                    </Form.Group>

                    <Form.Group className="mb-3">
                        <Form.Label>Изображение</Form.Label>
                        <Form.Control
                            type="file"
                            onChange={e => setFile(e.target.files[0])}
                        />
                    </Form.Group>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={onHide}>Закрыть</Button>
                <Button variant="primary" onClick={handleUpdate}>Сохранить</Button>
            </Modal.Footer>
        </Modal>
    );
});

export default EditPrinterModal;
