import React, {useEffect, useState, useContext} from 'react';
import {Button, Card, Col, Container, Image, Row} from "react-bootstrap";
import bigStar from '../assets/bigStar.png'
import {useParams, useNavigate} from 'react-router-dom'
import {fetchOnePrinter, deletePrinter, editPrinter} from "../http/printerAPI";
import {Context} from "../index";

const PrinterPage = () => {
    const [printer, setPrinter] = useState({info: []})
    const {id} = useParams()
    const navigate = useNavigate()
    const {user} = useContext(Context)

    useEffect(() => {
        fetchOnePrinter(id).then(data => setPrinter(data))
    }, [id])

    const handleDelete = async () => {
        if (window.confirm('Вы действительно хотите удалить этот принтер?')) {
            try {
                await deletePrinter(id)
                navigate('/')
            } catch (e) {
                alert('Ошибка при удалении: ' + e.response?.data?.message)
            }
        }
    }

    const handleEdit = async () => {
        try {
            await editPrinter(id)
            navigate('/')
        } catch (e) {
            alert('Ошибка при редактировании: ' + e.response?.data?.message)
        }
    }

    return (
        <Container className="mt-3">
            <Row>
                <Col md={4}>
                    <Image width={300} height={300} src={process.env.REACT_APP_API_URL + printer.img}/>
                </Col>
                <Col md={4}>
                    <Row className="d-flex flex-column align-items-center">
                        <h2>{printer.name}</h2>
                        <div
                            className="d-flex align-items-center justify-content-center"
                            style={{background: `url(${bigStar}) no-repeat center center`, width:240, height: 240, backgroundSize: 'cover', fontSize:64}}
                        >
                            {printer.rating}
                        </div>
                    </Row>
                </Col>
                <Col md={4}>
                    <Card
                        className="d-flex flex-column align-items-center justify-content-around"
                        style={{width: 400, height: 300, fontSize: 32, border: '5px solid lightgray'}}
                    >
                        <h3>От: {printer.price} руб.</h3>

                        {user.isAuth && (
                            <>
                                <Button
                                    variant={"outline-info"}
                                    onClick={handleEdit}
                                    className="mt-2"
                                >
                                    Редактировать
                                </Button>
                                <Button
                                    variant={"outline-danger"}
                                    onClick={handleDelete}
                                    className="mt-2"
                                >
                                    Удалить принтер
                                </Button>
                            </>
                        )}
                        {!user.isAuth && (
                                <h6>Что бы вносить изменения вы должны быть авторизированным пользователем</h6>
                        )}
                    </Card>
                </Col>
            </Row>
            <Row className="d-flex flex-column m-3">
                <h1>Характеристики</h1>
                {printer.info.map((info, index) =>
                    <Row key={info.id} style={{background: index % 2 === 0 ? 'lightgray' : 'transparent', padding: 10}}>
                        {info.title}: {info.description}
                    </Row>
                )}
            </Row>
        </Container>
    );
};

export default PrinterPage;
