import React, {useEffect, useState} from 'react';
import {Button, Card, Col, Container, Image, Row, Modal} from 'react-bootstrap';
import bigStar from '../assets/bigStar.png';
import {useParams, useNavigate} from 'react-router-dom';
import {fetchOnePrinter, deletePrinter, updatePrinter} from "../http/printerAPI";
import CreatePrinter from '../components/modals/CreatePrinter';
import EditPrinterModal from '../components/modals/EditPrinterModal';

const PrinterPage = () => {
    const [printer, setPrinter] = useState({info: []});
    const [error, setError] = useState(null);
    const [showEditModal, setShowEditModal] = useState(false);
    const {id} = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        fetchOnePrinter(id).then(data => setPrinter(data));
    }, [id]);

    const handleDelete = async () => {
        if (!window.confirm('Вы точно хотите удалить этот принтер?')) return;

        try {
            await deletePrinter(id);
            navigate('/');
        } catch (e) {
            setError(e.response?.data?.message || 'Ошибка при удалении');
        }
    };

    const handleUpdate = async (updatedPrinter) => {
        try {
            const formData = new FormData();
            formData.append('name', updatedPrinter.name);
            formData.append('price', updatedPrinter.price);
            if (updatedPrinter.newImage) {
                formData.append('img', updatedPrinter.newImage);
            }
            formData.append('brandId', updatedPrinter.brandId);
            formData.append('printTechnologyId', updatedPrinter.printTechnologyId);
            formData.append('info', JSON.stringify(updatedPrinter.info));

            await updatePrinter(id, formData);
            const data = await fetchOnePrinter(id);
            setPrinter(data);
            setShowEditModal(false);
        } catch (e) {
            setError(e.response?.data?.message || 'Ошибка при обновлении');
        }
    };

    return (
        <Container className="mt-3">
            {error && <Alert variant="danger">{error}</Alert>}

            <Row>
                <Col md={4}>
                    <Image width={300} height={300} src={process.env.REACT_APP_API_URL + printer.img}/>
                </Col>
                <Col md={4}>
                    <Row className="d-flex flex-column align-items-center">
                        <h2>{printer.name}</h2>
                        <div className="rating-circle">
                            {printer.rating}
                        </div>
                    </Row>
                </Col>
                <Col md={4}>
                    <Card className="printer-actions">
                        <h3>От: {printer.price} руб.</h3>

                        <Button
                            variant="outline-primary"
                            onClick={() => setShowEditModal(true)}
                            className="mt-2"
                        >
                            Редактировать
                        </Button>

                        <Button
                            variant="outline-danger"
                            onClick={handleDelete}
                            className="mt-2"
                        >
                            Удалить
                        </Button>
                    </Card>
                </Col>
            </Row>

            <Row className="specs-section">
                <h1>Характеристики</h1>
                {printer.info.map((info, index) => (
                    <Row key={info.id} className={`spec-item ${index % 2 === 0 ? 'even' : 'odd'}`}>
                        {info.title}: {info.description}
                    </Row>
                ))}
            </Row>

            <Modal show={showEditModal} onHide={() => setShowEditModal(false)} size="lg">
                <Modal.Header closeButton>
                    <Modal.Title>Редактировать принтер</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <CreatePrinter
                        printerToEdit={printer}
                        onSuccess={handleUpdate}
                        onHide={() => setShowEditModal(false)}
                    />
                </Modal.Body>
            </Modal>
        </Container>
    );

};

export default PrinterPage;
