import React, { useContext, useEffect, useState } from 'react';
import { Modal, Button, Form, Row, Col, Dropdown } from 'react-bootstrap';
import { Context } from '../../index';
import { observer } from 'mobx-react-lite';
import { createPrinter, updatePrinter, fetchBrands, fetchPrint_Technologys } from '../../http/printerAPI';


const CreatePrinter = observer(({ show, onHide, printerToEdit }) => {
    const { printer } = useContext(Context);
    const [name, setName] = useState('');
    const [price, setPrice] = useState(0);
    const [file, setFile] = useState(null);
    const [info, setInfo] = useState([]);
    const [preview, setPreview] = useState(null);
    const [isLoading, setIsLoading] = useState(false);

    // Безопасные геттеры для выбранных значений
    const getSelectedBrand = () => printer.selectedBrand || {};
    const getSelectedTechnology = () => printer.selectedPrint_Technology || {};

    useEffect(() => {
        // Загружаем справочники
        fetchBrands().then(data => printer.setBrands(data || []));
        fetchPrint_Technologys().then(data => printer.setPrint_Technologys(data || []));

        if (printerToEdit) {
            // Устанавливаем значения для редактирования
            setName(printerToEdit.name || '');
            setPrice(printerToEdit.price || 0);
            setInfo(printerToEdit.info || []);

            if (printerToEdit.img) {
                setPreview(process.env.REACT_APP_API_URL + printerToEdit.img);
            }

            // Находим и устанавливаем выбранный бренд
            if (printerToEdit.brandId) {
                const foundBrand = printer.brands.find(b => b.id === printerToEdit.brandId);
                if (foundBrand) printer.setSelectedBrand(foundBrand);
            }

            // Находим и устанавливаем выбранную технологию
            if (printerToEdit.printTechnologyId) {
                const foundTech = printer.print_technologys.find(t => t.id === printerToEdit.printTechnologyId);
                if (foundTech) printer.setSelectedPrint_Technology(foundTech);
            }
        } else {
            // Сбрасываем значения для нового принтера
            setName('');
            setPrice(0);
            setFile(null);
            setInfo([]);
            setPreview(null);
            printer.setSelectedBrand({});
            printer.setSelectedPrint_Technology({});
        }
    }, [printerToEdit]);

    const handleSubmit = async () => {
        const brand = getSelectedBrand();
        const technology = getSelectedTechnology();

        if (!name || !price || !brand.id || !technology.id) {
            alert('Заполните все обязательные поля!');
            return;
        }

        setIsLoading(true);
        try {
            const formData = new FormData();
            formData.append('name', name);
            formData.append('price', `${price}`);
            if (file) formData.append('img', file);
            formData.append('brandId', brand.id);
            formData.append('printTechnologyId', technology.id);
            formData.append('info', JSON.stringify(info));

            if (printerToEdit) {
                await updatePrinter(printerToEdit.id, formData);
            } else {
                await createPrinter(formData);
            }
            onHide();
        } catch (e) {
            alert(e.response?.data?.message || 'Ошибка при сохранении');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <Modal show={show} onHide={onHide} size="lg" centered>
            <Modal.Header closeButton>
                <Modal.Title>
                    {printerToEdit ? 'Редактировать принтер' : 'Добавить новый принтер'}
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Row className="mb-3">
                        <Col md={6}>
                            <Dropdown>
                                <Dropdown.Toggle variant="outline-secondary" className="w-100">
                                    {getSelectedBrand().name || "Выберите бренд"}
                                </Dropdown.Toggle>
                                <Dropdown.Menu className="w-100">
                                    {printer.brands.map(brand => (
                                        <Dropdown.Item
                                            key={brand.id}
                                            onClick={() => printer.setSelectedBrand(brand)}
                                        >
                                            {brand.name}
                                        </Dropdown.Item>
                                    ))}
                                </Dropdown.Menu>
                            </Dropdown>
                        </Col>
                        <Col md={6}>
                            <Dropdown>
                                <Dropdown.Toggle variant="outline-secondary" className="w-100">
                                    {getSelectedTechnology().name || "Выберите технологию печати"}
                                </Dropdown.Toggle>
                                <Dropdown.Menu className="w-100">
                                    {printer.print_technologys.map(tech => (
                                        <Dropdown.Item
                                            key={tech.id}
                                            onClick={() => printer.setSelectedPrint_Technology(tech)}
                                        >
                                            {tech.name}
                                        </Dropdown.Item>
                                    ))}
                                </Dropdown.Menu>
                            </Dropdown>
                        </Col>
                    </Row>

                    <Form.Group className="mb-3">
                        <Form.Control
                            value={name}
                            onChange={e => setName(e.target.value)}
                            placeholder="Название принтера"
                        />
                    </Form.Group>


                <Form.Control
                    value={price}
                    onChange={e => setPrice(Number(e.target.value))}
                    className="mt-3"
                    placeholder="Введите стоимость устройства"
                    print_technology="number"
                />

                    <Form.Group className="mb-3">
                        <Form.Label>Изображение принтера</Form.Label>
                        <Form.Control
                            type="file"
                            onChange={e => setFile(e.target.files[0])}
                            accept="image/*"
                        />
                        {preview && (
                            <div className="mt-2">
                                <img
                                    src={preview}
                                    alt="Предпросмотр"
                                    style={{ maxHeight: '150px' }}
                                />
                            </div>
                        )}
                    </Form.Group>

                    <div className="d-flex justify-content-between align-items-center mb-3">
                        <h5>Характеристики:</h5>
                        <Button variant="outline-primary" size="sm" onClick={() => setInfo([...info, { title: '', description: '', number: Date.now() }])}>
                            + Добавить характеристику
                        </Button>
                    </div>

                    {info.map((item, index) => (
                        <Row key={item.number || index} className="mb-2">
                            <Col md={5}>
                                <Form.Control
                                    value={item.title}
                                    onChange={e => {
                                        const newInfo = [...info];
                                        newInfo[index].title = e.target.value;
                                        setInfo(newInfo);
                                    }}
                                    placeholder="Название"
                                />
                            </Col>
                            <Col md={5}>
                                <Form.Control
                                    value={item.description}
                                    onChange={e => {
                                        const newInfo = [...info];
                                        newInfo[index].description = e.target.value;
                                        setInfo(newInfo);
                                    }}
                                    placeholder="Значение"
                                />
                            </Col>
                            <Col md={2}>
                                <Button
                                    variant="outline-danger"
                                    onClick={() => setInfo(info.filter((_, i) => i !== index))}
                                >
                                    Удалить
                                </Button>
                            </Col>
                        </Row>
                    ))}
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={onHide}>
                    Отмена
                </Button>
                <Button variant="primary" onClick={handleSubmit} disabled={isLoading}>
                    {isLoading ? 'Сохранение...' : 'Сохранить'}
                </Button>
            </Modal.Footer>
        </Modal>
    );
});

export default CreatePrinter;
